def evaluate(exprInList):
    stack = []
    operatorBuilder = OperatorBuilder()
    for e in exprInList:
        if type(e) == bool:
            stack.append(e)
        else:
            op = operatorBuilder.buildFor(e)
            if op.isUnary():
                a = stack.pop()
                stack.append(op.apply(a))
            elif op.isBinary():
                a = stack.pop()
