class OperatorBuilder:
    def __init__(self):
        self._operatorDefinition = {
            Not().symbol: Not(),
            And().symbol: And(),
            Or().symbol: Or(),
            Nand().symbol: Nand(),
            Nor().symbol: Nor(),
            Xor().symbol: Xor(),
            Impl().symbol: Impl(),
            Equ().symbol: Equ()
